app.component("login",{controller:["$scope","$http","userSrv",function(s,o,t){s.signIn=function(){t.signIn({logemail:s.clientEmail,logpassword:s.password}).then(function(o){401==o&&(s.signInForm.password.$setValidity("correctPassword",!1),s.signInForm.clientEmail.$setValidity("correctPassword",!1))}),s.$watchGroup(["clientEmail","password"],function(){s.signInForm.clientEmail.$setValidity("correctPassword",!0),s.signInForm.password.$setValidity("correctPassword",!0)})}}],templateUrl:"public/dist/templates/login.html"});